import React, { Component } from 'react';
import { Badge, Card, CardBody, CardHeader, Col, Row, Table } from 'reactstrap';
//import {AppRegistry, ActivityIndicator, ListView,Text,View} from 'react-native';
import axios from 'axios';
import usersData from './UsersData'

function UserRow(props) {
  const user = props.user
  const userLink = `#/users/${user.id}`

  const getBadge = (status) => {
    return status === 'Active' ? 'success' :
      status === 'Inactive' ? 'secondary' :
        status === 'Pending' ? 'warning' :
          status === 'Banned' ? 'danger' :
            'primary'
  }

  return (
    <tr key={user.id.toString()}>
        <th scope="row"><a href={userLink}>{user.id}</a></th>
        <td><a href={userLink}>{user.name}</a></td>
        <td>{user.registered}</td>
        <td>{user.role}</td>
        <td><Badge href={userLink} color={getBadge(user.status)}>{user.status}</Badge></td>
    </tr>
  )
}

class Users extends Component {

  constructor(){
    super();
    this.state = {
      items:[],
      isLoaded:false
    }
   // this.fetchUser = this.fetchUser.bind(this);
  }
  componentDidMount() {
    fetch('http://laravel.local/api/users').then(res=>res.json()).then(json=>{

      this.setState({
        isLoaded:true,
        items:json,
      });
      console.log(json);
    })
  }
  render() {

    const userList = usersData.filter((user) => user.id < 12)
    console.log("dsada");
    var {isLoaded, items}=this.state;
    console.log(this.state.userid);
    return (
      <div className="animated fadeIn">

        <Row>
          <Col xl={6}>
            <Card>
              <CardHeader>
                <i className="fa fa-align-justify"></i> Users 
                <small className="text-muted">example</small>

              </CardHeader>
              <CardBody>
                <Table responsive hover>
                  <thead>
                    <tr>
                      <th scope="col">userid</th>
                      <th scope="col">name</th>
                      <th scope="col">registered</th>
                      <th scope="col">role</th>
                      <th scope="col">status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {userList.map((user, index) =>
                      <UserRow key={index} user={user}/>
                    )}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <ul>
        {this.state.items.map(item =>
          <li key={item.userid}>
            <a href={item.username}>{item.userpass}</a>
          </li>
        )}
      </ul>
      </div>

    )
  }
}

export default Users;
