import React, { Component } from 'react';
import { Badge, Card, CardBody, CardHeader, Col, Row, Table } from 'reactstrap';
//import {AppRegistry, ActivityIndicator, ListView,Text,View} from 'react-native';
import axios from 'axios';
import Popup from "reactjs-popup";
import Modal from '@trendmicro/react-modal';
import '@trendmicro/react-modal/dist/react-modal.css';
import usersData from './UsersData';
let bodyStyle = null;

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};
function UserRow(props) {
  const user = props.user
  const userLink = `#/users/${user.id}`

  const getBadge = (status) => {
    return status === 'Active' ? 'success' :
      status === 'Inactive' ? 'secondary' :
        status === 'Pending' ? 'warning' :
          status === 'Banned' ? 'danger' :
            'primary'
  }

  return (
    <tr key={user.userid.toString()}>
        <th scope="row"><a href={userLink}>{user.userid}</a></th>
        <td><a href={userLink}>{user.username}</a></td>
        <td>{user.userpass}</td>

    </tr>
  )
}

class Users extends Component {
  static propTypes = {
    ...Modal.propTypes
};
static defaultProps = {
    ...Modal.defaultProps
};

componentWillReceiveProps(nextProps) {
    if (nextProps.show !== this.props.show) {
        if (nextProps.show) {
            this.changeBodyStyle();
        } else {
            this.restoreBodyStyle();
        }
    }
}
componentDidMount() {
    this.changeBodyStyle();
}
componentWillUnmount() {
    this.restoreBodyStyle();
}
changeBodyStyle() {
    if (bodyStyle) {
        return;
    }
    // Prevent body from scrolling when a modal is opened
    const body = document.querySelector('body');
    bodyStyle = {
        overflowY: body.style.overflowY
    };
    body.style.overflowY = 'hidden';
}
restoreBodyStyle() {
    if (bodyStyle) {
        const body = document.querySelector('body');
        body.style.overflowY = bodyStyle.overflowY;
        bodyStyle = null;
    }
}
  constructor(){
    super();
    this.state = {
      items:[],
      isLoaded:false,
      showModal: false
    };


    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }
  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }
  componentDidMount() {
    fetch('http://laravel.local/api/getusers').then(res=>res.json()).then(json=>{
    console.log(json.userid);
      this.setState({
        items:json,
      });
    })
  }
  render() {
    const { onClose, ...props } = this.props;

    const { items }= this.state;
    console.log("geldim");
    console.log(items.username);
    const userList = usersData.filter((user) => user.id < 12)
    console.log("dsada");
    //var {isLoaded, items}=this.state;

    return (
      <div className="animated fadeIn">

        <Row>
          <Col xl={6}>
            <Card>
              <CardHeader>
                <i className="fa fa-align-justify"></i> Users 
                <small className="text-muted">example</small>

              </CardHeader>
              <CardBody>
                <Table responsive hover>
                  <thead>
                    <tr>
                      <th scope="col">userid</th>
                      <th scope="col">username</th>
                      <th scope="col">userpass</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map((user, index) =>
                      <UserRow key={index} user={user}/>
                    )}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <div>
        <button onClick={this.openModal}>Open Modal</button>
        <Modal
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <h2 ref={subtitle => this.subtitle = subtitle}></h2>

          <div>I am a modal</div>
          <form>
            <input />
            <button>tab navigation</button>
            <button>stays</button>
            <button>inside</button>
            <button>the modal</button>
            <button onClick={this.closeModal}>close</button>
          </form>
        </Modal>
      </div>
      <Modal
                {...props}
                onClose={() => {
                    this.restoreBodyStyle();
                    onClose();
                }}
            />
      </div>

    )

  }
}

Users.Overlay = Modal.Overlay;
Users.Content = Modal.Content;
Users.Header = Modal.Header;
Users.Title = Modal.Title;
Users.Body = Modal.Body;
Users.Footer = Modal.Foote
export default Users;
